import React, { useState, useEffect } from "react";

interface Event {
  _id: string;
  title: string;
  description: string;
  date: string;
  location: string;
  capacity: number;
  attendees: string[];
}

const MyEvents: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [userId, setUserId] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const payload = JSON.parse(atob(token.split(".")[1]));
      setUserId(payload.userId);
      fetchMyEvents(payload.userId);
    }
  }, []);

  const fetchMyEvents = async (userId: string) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:5001/api/events/user/${userId}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      setEvents(data);
    } catch (error) {
      console.error("Error fetching my events:", error);
    }
  };

  const handleCancelRegistration = async (eventId: string) => {
    try {
      const token = localStorage.getItem("token");
      await fetch(`http://localhost:5001/api/events/${eventId}/cancel`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      fetchMyEvents(userId);
    } catch (error) {
      console.error("Error cancelling registration:", error);
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {events.map((event) => (
        <div key={event._id} className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-xl font-bold">{event.title}</h3>
          <p className="text-gray-600">{event.description}</p>
          <p className="text-sm text-gray-500">
            Date: {new Date(event.date).toLocaleString()}
          </p>
          <p className="text-sm text-gray-500">Location: {event.location}</p>
          <p className="text-sm text-gray-500">
            Capacity: {event.attendees.length}/{event.capacity}
          </p>
          <button
            onClick={() => handleCancelRegistration(event._id)}
            className="mt-2 bg-red-500 text-white px-4 py-2 rounded"
          >
            Cancel Registration
          </button>
        </div>
      ))}
    </div>
  );
};

export default MyEvents;
