import React, { useState, useEffect } from "react";

interface Event {
  _id: string;
  title: string;
  description: string;
  date: string;
  location: string;
  capacity: number;
  attendees: string[];
}

const Events: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [location, setLocation] = useState("");
  const [capacity, setCapacity] = useState("");

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/events");
      const data = await response.json();
      setEvents(data);
    } catch (error) {
      console.error("Error fetching events:", error);
    }
  };

  const handleCreateEvent = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await fetch("http://localhost:5001/api/events", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          title,
          description,
          date,
          location,
          capacity: parseInt(capacity),
        }),
      });
      fetchEvents();
      setTitle("");
      setDescription("");
      setDate("");
      setLocation("");
      setCapacity("");
    } catch (error) {
      console.error("Error creating event:", error);
    }
  };

  const handleRegister = async (eventId: string) => {
    try {
      const token = localStorage.getItem("token");
      await fetch(`http://localhost:5001/api/events/${eventId}/register`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      fetchEvents();
    } catch (error) {
      console.error("Error registering for event:", error);
    }
  };

  return (
    <div>
      <div className="max-w-md mx-auto mb-8 p-6 bg-white rounded-lg shadow">
        <h2 className="text-2xl font-bold mb-4">Create Event</h2>
        <form onSubmit={handleCreateEvent}>
          <div className="mb-4">
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="w-full px-3 py-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <textarea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full px-3 py-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <input
              type="datetime-local"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              className="w-full px-3 py-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <input
              type="text"
              placeholder="Location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              className="w-full px-3 py-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <input
              type="number"
              placeholder="Capacity"
              value={capacity}
              onChange={(e) => setCapacity(e.target.value)}
              className="w-full px-3 py-2 border rounded"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded"
          >
            Create Event
          </button>
        </form>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {events.map((event) => (
          <div key={event._id} className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-xl font-bold">{event.title}</h3>
            <p className="text-gray-600">{event.description}</p>
            <p className="text-sm text-gray-500">
              Date: {new Date(event.date).toLocaleString()}
            </p>
            <p className="text-sm text-gray-500">Location: {event.location}</p>
            <p className="text-sm text-gray-500">
              Capacity: {event.attendees.length}/{event.capacity}
            </p>
            <button
              onClick={() => handleRegister(event._id)}
              className="mt-2 bg-green-500 text-white px-4 py-2 rounded"
            >
              Register
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
